import { useState, useEffect } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const ViewBankCustomers = () => {
  let navigate = useNavigate();
  const [allCustomer, setAllCustomer] = useState([]);
  const bank = JSON.parse(sessionStorage.getItem("active-bank"));

  const [customerName, setCustomerNumber] = useState("");

  const [tempCustomerName, setTempCustomerName] = useState("");

  const bank_jwtToken = sessionStorage.getItem("bank-jwtToken");

  const [updateUserStatusRequest, setUpdateUserStatusRequest] = useState({
    userId: "",
    status: "",
  });

  const retrieveAllCustomers = async () => {
    const response = await axios.get(
      "http://localhost:8080/api/user/bank/customers?bankId=" + bank.bank.id,
      {
        headers: {
          Authorization: "Bearer " + bank_jwtToken, // Replace with your actual JWT token
        },
      }
    );
    console.log(response.data);
    return response.data;
  };

  const retrieveBankAllCustomerByName = async () => {
    const response = await axios.get(
      "http://localhost:8080/api/user/bank/customer/search?bankId=" +
        bank.bank.id +
        "&customerName=" +
        customerName,
      {
        headers: {
          Authorization: "Bearer " + bank_jwtToken, // Replace with your actual JWT token
        },
      }
    );
    console.log(response.data);
    return response.data;
  };

  useEffect(() => {
    if (customerName !== "") {
      const getAllCustomersByName = async () => {
        const customers = await retrieveBankAllCustomerByName();
        if (customers) {
          setAllCustomer(customers.users);
        }
      };
      getAllCustomersByName();
    } else {
      const getAllCustomers = async () => {
        const customers = await retrieveAllCustomers();
        if (customers) {
          setAllCustomer(customers.users);
        }
      };

      getAllCustomers();
    }
  }, [customerName]);

  const searchBankCustomersByName = (e) => {
    e.preventDefault();
    setCustomerNumber(tempCustomerName);
  };

  const viewAccountDetails = (customer) => {
    navigate("/customer/bank/account/detail", { state: customer });
  };

  const addBankAccountPage = (customer) => {
    navigate("/bank/customer/account/add", { state: customer });
  };

  const activateUser = (userId, e) => {
    updateUserStatusRequest.userId = userId;
    updateUserStatusRequest.status = "Active";

    fetch("http://localhost:8080/api/user/update/status", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Authorization: "Bearer " + bank_jwtToken,
      },
      body: JSON.stringify(updateUserStatusRequest),
    })
      .then((result) => {
        console.log("result", result);
        result.json().then((res) => {
          console.log(res);

          if (res.success) {
            console.log("Got the success response");

            toast.success(res.responseMessage, {
              position: "top-center",
              autoClose: 1000,
              hideProgressBar: false,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: true,
              progress: undefined,
            });

            setTimeout(() => {
              window.location.reload(true);
            }, 1000); // Redirect after 3 seconds
          } else {
            console.log("Didn't got success response");
            toast.error("It seems server is down", {
              position: "top-center",
              autoClose: 1000,
              hideProgressBar: false,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: true,
              progress: undefined,
            });
            setTimeout(() => {
              window.location.reload(true);
            }, 1000); // Redirect after 3 seconds
          }
        });
      })
      .catch((error) => {
        console.error(error);
        toast.error("It seems server is down", {
          position: "top-center",
          autoClose: 1000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
        });
        setTimeout(() => {
          window.location.reload(true);
        }, 1000); // Redirect after 3 seconds
      });
  };

  const deactivateUser = (userId) => {
    updateUserStatusRequest.userId = userId;
    updateUserStatusRequest.status = "Deactivated";

    fetch("http://localhost:8080/api/user/update/status", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Authorization: "Bearer " + bank_jwtToken,
      },
      body: JSON.stringify(updateUserStatusRequest),
    })
      .then((result) => {
        console.log("result", result);
        result.json().then((res) => {
          console.log(res);

          if (res.success) {
            console.log("Got the success response");

            toast.success(res.responseMessage, {
              position: "top-center",
              autoClose: 1000,
              hideProgressBar: false,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: true,
              progress: undefined,
            });

            setTimeout(() => {
              window.location.reload(true);
            }, 1000); // Redirect after 3 seconds
          } else {
            console.log("Didn't got success response");
            toast.error("It seems server is down", {
              position: "top-center",
              autoClose: 1000,
              hideProgressBar: false,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: true,
              progress: undefined,
            });
            setTimeout(() => {
              window.location.reload(true);
            }, 1000); // Redirect after 3 seconds
          }
        });
      })
      .catch((error) => {
        console.error(error);
        toast.error("It seems server is down", {
          position: "top-center",
          autoClose: 1000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
        });
        setTimeout(() => {
          window.location.reload(true);
        }, 1000); // Redirect after 3 seconds
      });
  };

  return (
    <div>
      <div className="mt-2">
        <div
          className="card form-card ms-5 me-5 mb-5 custom-bg border-color "
          style={{
            height: "45rem",
          }}
        >
          <div className="card-header custom-bg-text text-center bg-color">
            <h2>All Bank Customers</h2>
          </div>
          <div
            className="card-body"
            style={{
              overflowY: "auto",
            }}
          >
            <div className="row">
              <div className="col">
                <form class="row g-3 align-items-center">
                  <div class="col-auto">
                    <label>
                      <b>Customer Name</b>
                    </label>
                    <input
                      type="text"
                      class="form-control"
                      placeholder="Enter customer name..."
                      onChange={(e) => setTempCustomerName(e.target.value)}
                      value={tempCustomerName}
                      required
                    />
                  </div>

                  <div class="col-auto">
                    <button
                      type="submit"
                      class="btn bg-color custom-bg-text btn-lg"
                      onClick={searchBankCustomersByName}
                    >
                      Search
                    </button>
                  </div>
                </form>
              </div>
            </div>

            <div className="table-responsive mt-3">
              <table className="table table-hover text-color text-center">
                <thead className="table-bordered border-color bg-color custom-bg-text">
                  <tr>
                    <th scope="col">Customer Name</th>
                    <th scope="col">Bank Name</th>
                    <th scope="col">Email</th>
                    <th scope="col">Gender</th>
                    <th scope="col">Contact</th>
                    <th scope="col">Street</th>
                    <th scope="col">City</th>
                    <th scope="col">Pincode</th>
                    <th scope="col">Account Details</th>
                    <th scope="col">Status</th>
                    <th scope="col">Action</th>
                  </tr>
                </thead>
                <tbody>
                  {allCustomer.map((customer) => {
                    return (
                      <tr>
                        <td>
                          <b>{customer.name}</b>
                        </td>
                        <td>
                          <b>{customer.bank.name}</b>
                        </td>
                        <td>
                          <b>{customer.email}</b>
                        </td>
                        <td>
                          <b>{customer.gender}</b>
                        </td>
                        <td>
                          <b>{customer.contact}</b>
                        </td>
                        <td>
                          <b>{customer.street}</b>
                        </td>
                        <td>
                          <b>{customer.city}</b>
                        </td>
                        <td>
                          <b>{customer.pincode}</b>
                        </td>
                        <td>
                          {(() => {
                            if (customer.isAccountLinked === "Yes") {
                              return (
                                <button
                                  onClick={() => viewAccountDetails(customer)}
                                  className="btn btn-sm bg-color custom-bg-text"
                                >
                                  View Account
                                </button>
                              );
                            }
                          })()}

                          {(() => {
                            if (customer.isAccountLinked !== "Yes") {
                              return (
                                <button
                                  onClick={() => addBankAccountPage(customer)}
                                  className="btn btn-sm bg-color custom-bg-text"
                                >
                                  Add Account
                                </button>
                              );
                            }
                          })()}
                        </td>
                        <td>
                          <b>{customer.status}</b>
                        </td>
                        <td>
                          {(() => {
                            if (customer.status === "Active") {
                              return (
                                <button
                                  onClick={() => deactivateUser(customer.id)}
                                  className="btn btn-sm bg-color custom-bg-text ms-2"
                                >
                                  Deactivate
                                </button>
                              );
                            }
                          })()}

                          {(() => {
                            if (customer.status !== "Active") {
                              return (
                                <button
                                  onClick={() => activateUser(customer.id)}
                                  className="btn btn-sm bg-color custom-bg-text ms-2"
                                >
                                  Activate
                                </button>
                              );
                            }
                          })()}

                          <ToastContainer />
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ViewBankCustomers;
